pragma solidity ^0.5.0;

/// @title A smart contract to simulate borrowing books from the library.
/// @author Jireh (based Pet Shop Tutorial)

contract Borrow {
    bool private emergency = false;
    address private admin;

    uint public bookCount;
    address[16] public borrowers;
    bool[16] public bookStatus;
    //mapping (uint => address) public borrowers;

    event LogBorrowed(uint bookId);
    event LogReturned(uint bookId);

    modifier onlyAdmin {
        require(msg.sender == admin);
        _;
    }

    modifier stopInEmergency {
        require(!emergency);
        _;
    }

    modifier onlyInEmergency {
        require(emergency);
        _;
    }

    constructor() public {
        //initialize bookCount as 15 (0th index)
        bookCount = 16;
    }

    /// @return circuit breaker design pattern, disables other functions
    function toggleEmergency() public onlyAdmin returns (bool) {
        emergency = !emergency;
        return true;
    }

    /// @return return ID of book that has been borrowed
    function borrowBook(uint bookId) public stopInEmergency returns (uint) {
        require(bookId >= 0 && bookId <= bookCount);
        require(borrowers[bookId] == 0x0000000000000000000000000000000000000000);
        borrowers[bookId] = msg.sender;
        bookStatus[bookId] = true;
        emit LogBorrowed(bookId);
        return bookId;
    }

    /// @return return ID of book that has been returned
    function returnBook(uint bookId) public stopInEmergency returns (uint) {
        require(bookId >= 0 && bookId <= bookCount);
        require(borrowers[bookId] != 0x0000000000000000000000000000000000000000);
        borrowers[bookId] = msg.sender;
        bookStatus[bookId] = false;
        emit LogReturned(bookId);
        return bookId;
    }

    /// @return total number of books
    function getNumBooks() public view stopInEmergency returns(uint) {
        return bookCount;
    }

    /// @return get address array of all borrowers
    function getBorrowers() public view stopInEmergency returns (address[16] memory) {
        // address[] memory ret = new address[](bookCount);
        // for (uint i = 0; i < bookCount; i++) {
        //     ret[i] = borrowers[i];
        // }
        // return ret;
        return borrowers;
    }

    /// @return get bool array of books' borrow status
    function getBookStatus() public view stopInEmergency returns (bool[16] memory) {
        return bookStatus;
    }

    /// @return get borrow status of book in book array
    function getSingleBookStatus(uint bookId) public view stopInEmergency returns (bool) {
        return bookStatus[bookId];
    }
}
